<!-- Desktop mega menu functionality -->
<script>
    document.addEventListener('DOMContentLoaded', () => {
        const navbarMegaMenu          = document.querySelector('.navbar-mega-menu'),
              megaMenu                = document.querySelector('.mega-menu'),
              megaMenuUnderline       = megaMenu.querySelector('.nav_underline'),
              megaMenuDropdownContent = document.querySelector('.mega-menu-content .content');

        let prevTarget = null;
        let prevActiveContent = null;
        
        const getWidthWithoutPadding = (element) => {
            const computedStyle = getComputedStyle(element),
                  paddingLeft   = parseFloat(computedStyle.paddingLeft),
                  paddingRight  = parseFloat(computedStyle.paddingRight),
                  elementWidth  = element.getBoundingClientRect().width;

            return elementWidth - paddingLeft - paddingRight;
        };
    
        window.addEventListener('scroll', () => {
            const scrollY = window.scrollY,
                scrollRange = navbarMegaMenu.offsetHeight,
                color = getComputedStyle(navbarMegaMenu).getPropertyValue('--_background-color-rgb') ? getComputedStyle(navbarMegaMenu).getPropertyValue('--_background-color-rgb') : '249, 249, 250';

            var opacityPercentageBackground = Math.min(scrollY / scrollRange, 1) * 90,
                opacityPercentageShadow     = Math.min(scrollY / scrollRange, 1) * 45;
            

            navbarMegaMenu.style.backgroundColor = `rgba(${color}, ${opacityPercentageBackground}%)`;
            navbarMegaMenu.style.boxShadow = `0px 4px 4px 0px rgba(227,233,236,${opacityPercentageShadow}%)`;
        })

        // Unfinished - close menu on click on larger screens
        // document.addEventListener('click', (event) => {
        //     var target = event.target

        //     if (target.matches('.dropdown-mega-menu.active > svg')) {
        //         megaMenuUnderline.style.width = 0;
        //         megaMenuDropdownContent.classList.remove('animate__in');
        //         megaMenuDropdownContent.querySelectorAll('.link-content').forEach(el => {
        //             el.classList.remove('slide__in');
        //             el.style = '';
        //         })
        //     }
        // });

        document.addEventListener('mouseover', (event) => {
            let target = event.target;

            if ((target.closest('.navbar-mega-menu') && !target.closest('.dropdown-mega-menu')) || (!target.closest('.navbar-mega-menu') && !target.closest('.mega-menu-content'))) {
                megaMenuUnderline.style.width = 0;
                megaMenuDropdownContent.classList.remove('animate__in');
                megaMenuDropdownContent.querySelectorAll('.link-content').forEach(el => {
                    el.classList.remove('slide__in');
                    el.style = '';
                })
            }

            if (!target.closest('.mega-menu-content')) {
                prevTarget?.classList.remove('active');

                if (prevTarget && target.closest('.dropdown-mega-menu')) {
                    let id = target.closest('.dropdown-mega-menu').id;
                    
                    if ( id != prevTarget.id ) {
                        let activeContent = document.querySelector(`.mega-menu-content #${id}`);

                        document.querySelectorAll('.link-content').forEach( el => {
                            el.classList.remove('slide__in')
                        })

                        prevActiveContent?.classList.remove('slide__in');
                        activeContent.classList.add('slide__in')

                        prevActiveContent = activeContent;

                        if (activeContent.nextElementSibling) {
                            activeContent.nextElementSibling.style = "transform: translate(13rem, 0)";
                        }
                        if (activeContent.previousElementSibling) {
                            activeContent.previousElementSibling.style = "transform: translate(-13rem, 0)";
                        }
                    }
                }
            }

            if (target.matches('li.dropdown-mega-menu:not(.nav-item-sm), li.dropdown-mega-menu:not(.nav-item-sm) *')) {
                target = target.closest('li.dropdown-mega-menu');

                const id            = target.id,
                      left          = target.offsetLeft,
                      paddingLeft   = parseInt(getComputedStyle(target).paddingLeft),
                      position      = left + paddingLeft;
                
                let activeContent   = megaMenuDropdownContent.querySelector(`#${id}`);

                target.classList.add('active');
                megaMenuDropdownContent.classList.add('animate__in');
                activeContent.classList.add('slide__in');

                if (activeContent.nextElementSibling) {
                    activeContent.nextElementSibling.style = "transform: translate(13rem, 0)";
                }
                if (activeContent.previousElementSibling) {
                    activeContent.previousElementSibling.style = "transform: translate(-13rem, 0)";
                }

                megaMenuUnderline.style.width = `${getWidthWithoutPadding(target) - 8}px`;
                megaMenuUnderline.style.transform = `translateX(${position}px)`;

                prevTarget = target;
            }

        });
    });
</script>

<!-- Mobile mega menu functionality -->
<script>
    const navbarMegaMenu = document.querySelector('.navbar-mega-menu'),
          menuBtn = document.querySelector('.mega-menu-btn'),
          menu = document.querySelector('.menu-container'),
          dropdownsSelector = '.dropdown-mega-menu.nav-item-sm',
          dropdowns = document.querySelectorAll(dropdownsSelector);

    let menuOpen = false;

    menuBtn.addEventListener('click', () => {
        dropdowns.forEach((el) => {
            el.nextElementSibling.classList.remove('menu-show')
        })
        menu.classList.toggle('mega-menu-show');
    });

    function handleClick(target) {
        const width = window.innerWidth;

        if (width <= 768) {
            const nextElement = target.nextElementSibling;
            
            if (nextElement.classList.contains('menu-show')) {
                nextElement.classList.toggle('menu-show');
            } else {
                dropdowns.forEach(el => el.nextElementSibling.classList.remove('menu-show'));
                nextElement.classList.toggle('menu-show');
            }
        }
    }

    document.addEventListener('click', (event) => {
        var target = event.target

        if (target.matches(dropdownsSelector)) {
            handleClick(target)
        }
    });

    const menuItemSm = document.querySelectorAll(".nav-item-sm");

    menuItemSm.forEach((btn) => {
        btn.addEventListener("click", (e) => {
            [...menuItemSm].filter(f => f != e.target).forEach(f => f.classList.remove('active'));

            e.target.classList.toggle('active');
        });
    });

</script>

